mapper.select:
SELECT s.*, singer.name as singerName, album.name as albumName FROM songs s LEFT JOIN singers singer ON s.singer_id = singer.id LEFT JOIN albums album ON s.album_id = album.id WHERE s.status = 1
AND s.name LIKE CONCAT('%', #{name}, '%')
AND s.singer_id = #{singerId}
AND s.album_id = #{albumId}
ORDER BY s.create_time DESC

SELECT s.*, singer.name as singerName, album.name as albumName FROM songs s LEFT JOIN singers singer ON s.singer_id = singer.id LEFT JOIN albums album ON s.album_id = album.id WHERE s.status = 1
AND ( s.name LIKE CONCAT('%', #{keyword}, '%') OR singer.name LIKE CONCAT('%', #{keyword}, '%') OR album.name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY CASE WHEN s.name LIKE CONCAT(#{keyword}, '%') THEN 1 WHEN singer.name LIKE CONCAT(#{keyword}, '%') THEN 2 WHEN s.name LIKE CONCAT('%', #{keyword}, '%') THEN 3 WHEN singer.name LIKE CONCAT('%', #{keyword}, '%') THEN 4 ELSE 5 END, s.play_count DESC LIMIT #{limit}
